# Install and load necessary libraries
if (!requireNamespace("BiocManager", quietly = TRUE)) install.packages("BiocManager")
BiocManager::install("Bioconductor")
BiocManager::install("Biostrings")

library(Bioconductor)
library(Biostrings)

# Define the function to process the target gene
processTargetGene <- function(targetGeneSequence) {
    # Split the target gene sequence into codons
    targetGene <- unlist(strsplit(targetGeneSequence, ""))
    codons <- sapply(X=1:(length(targetGene)/3),  FUN=function(i) paste(targetGene[(i-1)*3 + 1]:i*3))
    
    # Display information about the target gene
    cat("Sanity Check:\n",
        "sequence =", codons, "\n",
        "sequence length =", length(codons), "\n",
        "ORF length =", length(codons) - 2, "\n",
        "first ORF codon =", codons[3], "\n",
        "last ORF codon =", codons[length(codons) - 2], "\n")
}

# Example usage of the function with the provided target gene sequence
targetGeneSequence <- ""

# Call the function with the example target gene sequence
processTargetGene(targetGeneSequence)

